pragma solidity ^0.5.4;

import "./SafeMath.sol";
import "./Entitys.sol";

import "./ERC20.sol";
import "./ERC20Detailed.sol";

/***
    游戏合约
 */
contract lira  is ERC20, ERC20Detailed{
    modifier onlyOwner {
        require(msg.sender == _owner, "Only owner can call this function.");
        _;
    }

    using SafeMath for uint256;

    /**小数位数 */
    //assert(1 wei == 1);
    //assert(1 szabo == 1e12);
    //assert(1 finney == 1e15);
    //assert(1 ether == 1e18);
    uint256 private _trx_decimals = 6;
    uint256 private _price_upper_decimals = 18;

    /**平台费率 */
    uint256 private _fee_rate = 10;
    /**推荐费率 */
    uint256 private _recommend_rate = 10;
    address private _owner;

    /**分红比例 */
    uint256 private _bonus_rate = 45;

    /*返利 */
    uint256 private _feeback_rate = 5;
    //平台10% 推荐10% 返利5% 奖池30% 分红45%
    /**
        当前游戏
     */
    Entitys.GameStruct private current_game;

    //用户购买记录
    address[] private game_buy_users;

    /***
    游戏->用户->收益
    */
    mapping(address => Entitys.UserBenefitAndInvestment)
        private game_investment_benefit;
    /**
    游戏->用户->持有量
     */
    mapping(address => uint256) private game_user_buy_count;

    /**
    用户->推荐收益
     */
    mapping(address => uint256) private user_recommend_value;

    //零时用户变量
    mapping(address => uint256) private user_credit_log;

    //购买之后空投多少MTB给用户
    uint256 private buy_airdrop_mtb_count=uint256(1000000);

    constructor (
        uint256 _game_id,
        string memory _name,
        uint256 _game_times,
        uint256 _price,
        uint256 _price_upper_rate
    ) public ERC20Detailed("LIRA", "LIRA", uint8(_trx_decimals)){
        _owner = msg.sender;
        uint256 temp_now = now;
        uint256 _last_buy_time = temp_now;
        uint256 _over_time = temp_now.add(_game_times);
        uint256 _buy_price = _price * (10**uint256(_trx_decimals)); //1^6次方
        uint256 price_upper_rate =
            _price_upper_rate.mul(10**_price_upper_decimals); //1^18次方
        uint256 _next_change_rate_count = 70;
        current_game = Entitys.GameStruct({
            game_id: _game_id,
            name: _name,
            begin_time: temp_now,
            game_times: _game_times,
            last_buy_time: _last_buy_time,
            over_time: _over_time,
            base_prce: _buy_price,
            price: _buy_price,
            base_price_upper_rate: price_upper_rate,
            price_upper_rate: price_upper_rate,
            status: Entitys.GameStatus.Ing,
            prize_value: 0,
            bonus_value: 0,
            buy_count: 0,
            last_buy_user_address: _owner,
            next_change_rate_count: _next_change_rate_count
        });


          _mint(msg.sender, 10_000_000_000 * (10 ** uint256(decimals())));
    }

    /**
        获取现在时间
        返回当前的秒数
    */
    function getNow() public view returns (uint256 nowvalue) {
        return now;
    }

    /***
     *  获取小数位数
     * */
    function getDecimals() public view returns (uint256) {
        return _trx_decimals;
    }

    /***
     *  获取小数位数
     * */
    function getOwner() public view returns (address) {
        return _owner;
    }

    /**
     * 获取当前游戏信息
     */
    function getCurrentRoundInfo()
        public
        view
        returns (
            uint256 game_id,
            string memory name,
            uint256 begin_time,
            uint256 game_times,
            uint256 last_buy_time,
            uint256 over_time,
            uint256 price,
            uint256 price_upper_rate,
            Entitys.GameStatus status,
            uint256 prize_value,
            uint256 bonus_value,
            uint256 buy_count,
            address last_buy_user_address,
            uint256 next_change_rate_count
        )
    {
        Entitys.GameStruct memory game = current_game;
        game_id = game.game_id;
        name = game.name;
        begin_time = game.begin_time;
        game_times = game.game_times;
        last_buy_time = game.last_buy_time;
        over_time = game.over_time;
        price = game.price;
        price_upper_rate = game.price_upper_rate;
        status = game.status;
        prize_value = game.prize_value;
        buy_count = game.buy_count;
        bonus_value = game.bonus_value;
        last_buy_user_address = game.last_buy_user_address;
        next_change_rate_count = game.next_change_rate_count;
        return (
            game_id,
            name,
            begin_time,
            game_times,
            last_buy_time,
            over_time,
            price,
            price_upper_rate,
            status,
            prize_value,
            bonus_value,
            buy_count,
            last_buy_user_address,
            next_change_rate_count
        );
    }

    function askprice() public view returns (uint256 price) {
        Entitys.GameStruct memory game = current_game;
        return game.price;
    }

    //发送推荐奖励事件
    event SendRecommend(address bidder, uint256 amount, string event_msg);

    //发送返利奖励事件
    event SendFeeBack(address bidder, uint256 amount, string event_msg);
    //发送平台手续费事件
    event SendPlatformFee(address bidder, uint256 amount, string event_msg);
    //发送分红事件
    event CalcBonus(address bidder, uint256 amount, string event_msg);

    //发送事件
    event TransferAmount(address bidder, uint256 amount, string event_msg);

    //购买事件
    event BuyGameEvent(
        address bidder,
        uint256 amount,
        uint256 buy_time,
        string event_msg
    );

    function checkRecommend(address recommend_user)
        private
        view
        returns (bool)
    {
        if (recommend_user == address(0)) {
            return false;
        }
        if (recommend_user == _owner) {
            return true;
        }
        if (game_investment_benefit[recommend_user].isValue) {
            return true;
        }
        return false;
    }

    /**
     *@recommend_user 推荐用户的地址
     * 购买token
     *1TRX           ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1000000"
     *0.1TRX         ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====100000"
     *0.000001TRX    ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1"
     */
    function buy_token(address recommend_user)
        public
        payable
        returns (bool success)
    {
        require(checkRecommend(recommend_user), "recommend_user Error");
        // grab time
        //uint256 _now = now;
        Entitys.GameStruct memory game = current_game;
        require(game.game_id != uint256(0), "game not exist");
        //1 TRX           ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1000000"
        //0.1 TRX         ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====100000"
        //0.000001 TRX    ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1"
        uint256 amount = msg.value; //发送过来的钱
        address buyer = msg.sender; //购买者
        uint256 current_price = game.price; //当前的价格
        uint256 current_buy_count = game.buy_count; //总的购买数量
        if (current_buy_count == uint256(0)) {
            current_buy_count = 1;
        }
        //过滤超过300%分红率的用户
       current_buy_count=GetEffectiveBuyCount(current_buy_count);//计算有效用户数

        require(game.status == Entitys.GameStatus.Ing, "This Game Not Ing");
        require(game.over_time > now, "Game Is Over");
        //当前的价格必须小于支付的价格
        require(current_price <= amount, "This Game Buy Price is not limit");

        //推荐金额
        uint256 recommend_value = amount.mul(_recommend_rate).div(100);

        //平台抽取金额
        uint256 platform_value = amount.mul(_fee_rate).div(100);
        //分红金额
        uint256 bonus_value = amount.mul(_bonus_rate).div(100);

        //返利
        uint256 fee_back_value = amount.mul(_feeback_rate).div(100);

        //推荐用户
        address temp_recommend_user = _owner; //默认为平台用户
        //游戏-用户-投入/收益
        Entitys.UserBenefitAndInvestment memory bi =
            game_investment_benefit[buyer];

        temp_recommend_user = recommend_user; //赋值给零时变量
        bi.recommend_user_address = recommend_user; //赋值给存储

        if (bi.isValue && bi.recommend_user_address != address(0)) {
            temp_recommend_user = bi.recommend_user_address;
        }

        for (uint256 i = 0; i < game_buy_users.length; i++) {
            address temp_user = game_buy_users[i];
            delete user_credit_log[temp_user];
        }
        address(uint160(_owner)).transfer(platform_value); //发放平台手续费
        emit SendPlatformFee(_owner, platform_value, "发放平台手续费");

        address(uint160(temp_recommend_user)).transfer(recommend_value); //发放推荐奖励
        user_recommend_value[temp_recommend_user] = user_recommend_value[
            temp_recommend_user
        ]
            .add(recommend_value);

        emit SendRecommend(
            temp_recommend_user,
            recommend_value,
            "发放推荐奖励"
        );
        if (temp_recommend_user == _owner) {
            //推荐用户为平台用户
            address(uint160(_owner)).transfer(fee_back_value); //返利给到平台
            emit SendFeeBack(_owner, fee_back_value, "返利平台");
        } else {
            //推荐用户为平台用户
            address(uint160(buyer)).transfer(fee_back_value); //返利给到平台
            emit SendFeeBack(buyer, fee_back_value, "返利给用户");
        }

        uint256 used_bonus_value = uint256(0);

        
        //循环购买用户 进行分红
        for (uint256 i = 0; i < game_buy_users.length; i++) {
            address temp_user = game_buy_users[i];
            uint256 user_buy_count = game_user_buy_count[temp_user];
            //当前用户的分红量=总分红金额*份额/总份数
            uint256 temp_bonus_value =uint256(0);
            //判断用户能否参与分红
            if(CheckUserCanBonus(temp_user))
            {
               temp_bonus_value=bonus_value.mul(user_buy_count).div(current_buy_count);
            }
            //emit CalcBonus(temp_user,temp_bonus_value,"计算分红");
            used_bonus_value = used_bonus_value.add(temp_bonus_value);
            user_credit_log[temp_user] = user_credit_log[temp_user].add(
                temp_bonus_value
            );
        }
        //归入奖池的金额
        //奖池金额=购买额-推荐奖励-平台手续费-分红金额-返利
        uint256 temp_prize_value = amount.sub(recommend_value); //减去推荐
        temp_prize_value = temp_prize_value.sub(platform_value); //减去平台
        temp_prize_value = temp_prize_value.sub(used_bonus_value); //减去分红
        temp_prize_value = temp_prize_value.sub(fee_back_value); //减去返利

        if (bi.isValue) {
            bi = game_investment_benefit[buyer];
            bi.recommend_user_address = temp_recommend_user;
        } else {
            //收益-投入
            bi = Entitys.UserBenefitAndInvestment(
                0,
                0,
                temp_recommend_user,
                true
            );
            //把用户加入购买集合
            game_buy_users.push(buyer);
        }
        bi.touru = bi.touru.add(amount);
        //用户投入额与收益额增加
        game_investment_benefit[buyer] = bi;
        for (uint256 i = 0; i < game_buy_users.length; i++) {
            address temp_user = game_buy_users[i];
            uint256 t_amount = user_credit_log[temp_user]; //用户应得的分红
            //转RTX To User
            if (t_amount > 0) {
                //计算分红收益
                Entitys.UserBenefitAndInvestment memory temp_bi =
                    game_investment_benefit[temp_user];
                if (temp_bi.isValue) {
                    temp_bi.shouyi = temp_bi.shouyi.add(t_amount);
                } else {
                    //收益-投入
                    temp_bi = Entitys.UserBenefitAndInvestment(
                        t_amount,
                        0,
                        address(0),
                        true
                    );
                }
                game_investment_benefit[temp_user] = temp_bi;
                address(uint160(temp_user)).transfer(t_amount);
                emit TransferAmount(temp_user, t_amount, "发送分红奖励");
            }
        }
        for (uint256 i = 0; i < game_buy_users.length; i++) {
            address temp_user = game_buy_users[i];
            delete user_credit_log[temp_user];
        }
        //用户购买数+1
        game_user_buy_count[buyer] = game_user_buy_count[buyer].add(1); //当前用户总购买份数+1
        //更新游戏信息
        calcGameRate(buyer, temp_prize_value, used_bonus_value);

        emit BuyGameEvent(buyer, amount, now, "购买");
        
        //空投MTB给用户
        _transfer(_owner,buyer,buy_airdrop_mtb_count*(10 ** uint256(decimals())));
        return true;
    }

    /**
    更新游戏信息
     */
    function calcGameRate(
        address buyer,
        uint256 prize_value,
        uint256 used_bonus_value
    ) private returns (bool) {
        // grab time
        uint256 _now = now;
        Entitys.GameStruct memory game = current_game;
        //最后购买用户
        current_game.last_buy_user_address = buyer;
        //奖池金额
        current_game.prize_value = game.prize_value.add(prize_value); //游戏奖池金额=以前的奖池金额+本次的奖池金额

        //游戏购买人数
        uint256 current_buy_count = game.buy_count.add(1);
        current_game.buy_count = current_buy_count; //总购买份数+1

        //过滤收益超过300%的用户

        //最后购买时间
        current_game.last_buy_time = _now; //最后购买时间
        //游戏结束时间
        current_game.over_time = _now.add(game.game_times); //结束时间
        //70-140-280-540-1080-2196-->递增率减半
        if (current_buy_count == game.next_change_rate_count) {
            uint256 temp_price_rate = game.price_upper_rate.div(2); //自增率减半
            game.price_upper_rate = temp_price_rate;
            //下次递增率改变应次数增加
            game.next_change_rate_count = game.next_change_rate_count.mul(2);
            current_game.price_upper_rate = temp_price_rate;
            //下次递增率改变应次数增加
            current_game.next_change_rate_count = game.next_change_rate_count;
        }
        //游戏
        uint256 _price_upper_rate =
            game.price_upper_rate.div(10**_price_upper_decimals).add(100);
        uint256 current_price = game.price; //当前的价格
        //当前价格递增=当前价格*115/100
        current_game.price = current_price.mul(_price_upper_rate).div(100);

        //累加分红金额
        current_game.bonus_value = current_game.bonus_value.add(
            used_bonus_value
        );
    }

    /***
    获取有效的购买数
     */
    function GetEffectiveBuyCount(uint current_buy_count) private view returns (uint effective_count){
        effective_count=current_buy_count;
        //收益阈值
        uint256 threshold_value = uint256(3).mul(10**_trx_decimals);

        for (uint256 i = 0; i < game_buy_users.length; i++) {
            address temp_user = game_buy_users[i];
            if(temp_user==_owner)
            {
                continue;
            }
            //计算分红收益
            Entitys.UserBenefitAndInvestment memory temp_bi =
                game_investment_benefit[temp_user];
            //用户购买数
            uint256 user_buy_count = game_user_buy_count[temp_user];
            if (temp_bi.isValue && user_buy_count > 0) {
                //收益率
                uint256 shouyilv = temp_bi.shouyi.div(temp_bi.touru).mul(10**_trx_decimals);
                if (shouyilv >= threshold_value) {
                    effective_count = current_buy_count.sub(user_buy_count);
                }
            }
        }
        return effective_count;
    }

    /**
    判断用户是否可以分红
    */
    function CheckUserCanBonus(address user) private view returns (bool can_bonus) {
        can_bonus = true;
        if(user==_owner)
        {
            return true;
        }
        //收益阈值
        uint256 threshold_value = uint256(3).mul(10**_trx_decimals);
        //计算分红收益
        Entitys.UserBenefitAndInvestment memory temp_bi =
            game_investment_benefit[user];
        //用户购买数
        uint256 user_buy_count = game_user_buy_count[user];
        if (temp_bi.isValue && user_buy_count > 0) {
            //收益率
            uint256 shouyilv = temp_bi.shouyi.div(temp_bi.touru).mul(10**_trx_decimals);
            if (shouyilv >= threshold_value) {
                can_bonus=false;
            }
        }
        return can_bonus;
    }

    /***
     *  获取用户购买数量
     * */
    function getBuyerBuyCount(address buyer) public view returns (uint256) {
        return game_user_buy_count[buyer];
    }

    /**
     获取用户的收入与收益
     */
    function getBuerIncomeInvestment(address buyer)
        public
        view
        returns (
            uint256 income,
            uint256 investment,
            uint256 recommend_value,
            uint256 buy_count
        )
    {
        Entitys.UserBenefitAndInvestment memory bi =
            game_investment_benefit[buyer];
        income = bi.touru;
        investment = bi.shouyi;
        recommend_value = user_recommend_value[buyer];
        buy_count = game_user_buy_count[buyer];
        return (income, investment, recommend_value, buy_count);
    }

    function checkGameAndPrize() public view onlyOwner returns (bool) {
        require(_owner == msg.sender, "Must Owner Execute");
        uint256 _now = now;
        Entitys.GameStruct memory game = current_game;
        if (game.over_time <= _now) {
            return true;
        }
        return false;
    }

    function PrizeGame() public onlyOwner returns (bool) {
        require(_owner == msg.sender, "Must Owner Execute");
        // grab time
        uint256 _now = now;
        Entitys.GameStruct memory game = current_game;
        if (game.over_time <= _now) {
            //游戏结束
            if (game.last_buy_user_address != address(0)) {
                if (game.prize_value > 0) {
                    //发放奖励
                    address(uint160(game.last_buy_user_address)).transfer(
                        game.prize_value
                    ); //奖池全部发放
                }
            }
            //删除用户投入-收益
            for (uint256 i = 0; i < game_buy_users.length; i++) {
                address temp_user = game_buy_users[i];
                delete game_investment_benefit[temp_user];
                delete game_user_buy_count[temp_user];
                delete user_recommend_value[temp_user];
            }
            //删除购买激励
            delete game_buy_users;
            //开始新的游戏

            uint256 temp_now = now;
            uint256 _last_buy_time = temp_now;
            uint256 _over_time = temp_now.add(game.game_times);
            uint256 _buy_price = game.base_prce; //1^6次方
            uint256 price_upper_rate = game.base_price_upper_rate; //1^18次方
            uint256 next_change_rate_count = 70;
            uint256 game_id = game.game_id.add(1);
            current_game = Entitys.GameStruct(
                game_id,
                game.name,
                temp_now,
                game.game_times,
                _last_buy_time,
                _over_time,
                _buy_price,
                _buy_price,
                price_upper_rate,
                price_upper_rate,
                Entitys.GameStatus.Ing,
                0,
                0,
                0,
                _owner,
                next_change_rate_count
            );
            return true;
        }
        return false;
    }

    function kill() public onlyOwner returns (bool) {
        if (_owner == msg.sender) {
            // 权限检查
            Entitys.GameStruct memory game = current_game;
            if (game.prize_value > 0) {
                address(uint160(_owner)).transfer(game.prize_value);
                selfdestruct(msg.sender);
                return true;
            }
        }
        return false;
    
    }
    /**
    冻结或解冻账户
     */
    function freezeAccount(address target, bool freeze) public onlyOwner returns (bool)
    {   
        require(_owner != target, "can not freeze owner");
        return _freezeAccount(target,freeze);
    }



    /* 货币增发 */
    function mintToken(uint256 mintedAmount) public onlyOwner returns(bool) {
        _mint(_owner,mintedAmount * (10 ** uint256(decimals())));
        return true;
    }
}
